import 'package:flutter/material.dart';
import 'package:go_router/go_router.dart';
import '../../widgets/components/bottom_navigation_bar.dart';
import '../../widgets/components/post_item.dart';

class AllPostsScreen extends StatefulWidget {
  const AllPostsScreen({super.key});

  @override
  State<AllPostsScreen> createState() => _AllPostsScreenState();
}

class _AllPostsScreenState extends State<AllPostsScreen> {
  int _selectedIndex = 1; // 네비게이션바 인덱스

  void _onItemTapped(int index) { // 인덱스 상태관리
    setState(() {
      _selectedIndex = index;
    });
  }

// 더미 데이터
  final List<Map<String, dynamic>> dummyPosts = [
    {
      'name': '마이클',
      'contents': '선물로 받았어요',
      'imageUrl': [ ],
      'details': [
        {'식물 종': '몬스테라'},
        {'물 주기': '3일에 한번'},
        {'분갈이 주기': '2년'},
        {'환경': '19°C-30°C'},
      ],
    },
    {
      'name': '사라',
      'contents':
      '매우 긴 게시물.매우 긴 게시물.매우 긴 게시물.매우 긴 게시물.매우 긴 게시물.매우 긴 게시물.',
      'imageUrl': [
        'data:image/jpeg;base64,',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRNPqC2ao_lv3ZQTaJj-miKV_RSI_w2QFNt8w&s',
      ],
      'details': [
        {'식물 종': '선인장'},
        {'물 주기': '7일에 한번'},
        {'분갈이 주기': '3년'},
        {'환경': '19°C-30°C'},
      ],
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: _buildAppBar(), // 상단바
      body: ListView.builder(
        itemCount: dummyPosts.length,
        itemBuilder: (context, index) {
          final post = dummyPosts[index];
          final List<String> imageUrls = List<String>.from(post['imageUrl'] ?? []); // 이미지 없을경우 빈배열 처리
          return PostItem( // 각 게시물
            name: post['name'],
            contents: post['contents'],
            imageUrls: imageUrls,
            details: post['details'],
          );
        },
      ),
      bottomNavigationBar: MyBottomNavigationBar(
        selectedIndex: _selectedIndex,
        onItemTapped: _onItemTapped,
      ),
    );
  }

  // 상단 바
  AppBar _buildAppBar() {
    return AppBar(
      scrolledUnderElevation: 0,
      backgroundColor: Colors.white,
      title: Text(
        '전체 게시물',
        style: TextStyle(
          color: Colors.black,
          fontSize: 16,
          fontWeight: FontWeight.bold,
        ),
      ),
      actions: [ // 오른쪽 끝 배치
        _writeButton(), // 글쓰기 버튼
      ],
    );
  }

  // 글 쓰기 버튼 위젯
  Widget _writeButton() {
    return Padding(
      padding: EdgeInsets.fromLTRB(0, 8, 5, 0),
      child:
      Row(
        children: [
          Text(
            '글 쓰기',
            style: TextStyle(
              color: Colors.black,
              fontSize: 14,
              fontWeight: FontWeight.bold,
            ),
          ),
          IconButton(
            onPressed: () {
              context.push('/community/create'); // 게시물작성페이지로 이동
            },
            icon: Icon(
              Icons.add_circle,
              color: Color(0xFF4B7E5B),
              size: 35,
            ),
          ),
        ],
      ),
    );
  }
}